mapper.select:
SELECT mem_id, mem_pass, mem_role, mem_name, mem_phone, mem_nic, mem_profile, create_date
FROM member
WHERE mem_id = #{memId}

    	SELECT mem_id, mem_pass, mem_role, mem_name, mem_phone, mem_nic, mem_profile, create_date
		FROM member
		WHERE mem_phone = #{memPhone}
		AND mem_name = #{memName}

    	SELECT mem_id, mem_pass, mem_role, mem_name, mem_phone, mem_nic, mem_profile, create_date
		FROM member
		WHERE mem_id = #{memId}
		AND mem_phone = #{memPhone}

mapper.update:
    	UPDATE member
		SET mem_pass = #{memPass}
		WHERE mem_id = #{memId}

mapper.select:
SELECT mem_id, mem_pass, mem_role, mem_name, mem_phone, mem_nic, mem_profile, mem_addr, create_date
FROM member
WHERE mem_id = #{memId}
